                    continue;
                }

                if (item.labType === 'double') {
                    // Need 2 consecutive periods for double labs
                    if (period < maxHoursPerDay - 1 && 
                        !grid[period][day].subject && 
                        !grid[period + 1][day].subject &&
                        !(data.breakEnabled && (period + 1 === data.breakStart))) {

